import { Fragment } from 'react'
import { Menu, Transition } from '@headlessui/react'
import { ChevronDownIcon } from '@heroicons/react/20/solid'
import { useState,useEffect } from 'react'

function classNames(...classes) {
  return classes.filter(Boolean).join(' ')
}

export default function Example(props) {

 const [flag,setFlag] = useState()

   const handleButton = (e)=>{
     e.preventDefault();
     const { name, value } = e.target;
     console.log({[name]:value})
     setFlag(()=>{
        return {
          [name]:value
        }
     })

   }
    useEffect(()=>{
      props.flag(flag && flag.btn)

    },[flag])

  return (
    <Menu as="div" className="relative inline-block text-left">
        <Menu.Button className="inline-flex w-[14rem] justify-between gap-x-1.5 rounded-md bg-white px-3 py-2 text-sm font-dmsans text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50">
          Options
          <ChevronDownIcon className="-mr-1 h-5 w-5 text-gray-400" aria-hidden="true" />
        </Menu.Button>

      <Transition
        as={Fragment}
        enter="transition ease-out duration-100"
        enterFrom="transform opacity-0 scale-95"
        enterTo="transform opacity-100 scale-100"
        leave="transition ease-in duration-75"
        leaveFrom="transform opacity-100 scale-100"
        leaveTo="transform opacity-0 scale-95"
      >
        <Menu.Items className="absolute right-0 z-10 mt-2 w-56 origin-top-right rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none">
          <div className="py-1">
            <Menu.Item>
              {({ active }) => (
                <button
                  className={classNames(
                    active ? 'bg-gray-100 text-gray-900 ' : 'text-gray-700',
                    'block px-4 py-2 text-sm w-full text-left'
                  )}
                  value="Newest"
                  name="btn"
                  onClick={handleButton}
                >
                  Newest
                </button>
              )}
            </Menu.Item>
            <Menu.Item>
              {({ active }) => (
                <button
                  className={classNames(
                    active ? 'bg-gray-100 text-gray-900' : 'text-gray-700',
                    'block px-4 py-2 text-sm w-full text-left'
                  )}
                  name="btn"
                  onClick={handleButton}
                  value="Latest"
                >
                  Latest
                </button>
              )}
            </Menu.Item>


          </div>
        </Menu.Items>
      </Transition>
    </Menu>
  )
}
