import React, { useState } from "react";
import swal from "sweetalert";

async function LoginUser(credentials) {
  try {
    console.log(credentials)
    const response = await fetch("http://127.0.0.1:3001/api/v1/users/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(credentials),
    })
    console.log(response);

    if (response.status === 200) {
      swal({
        icon: "success",
        title: "Your work has been saved",
        showConfirmButton: false,
        timer: 1500,
      });

      window.setTimeout(() => {
        window.location.href = "/";
      }, 2000);

    } else {
      swal({
        icon: "error",
        title: "Oops...",
        text: "Email or/and password wrong!",
        footer: '<a href="">Why do I have this issue?</a>',
      });
      throw new Error();
    }
  } catch (error) {
    console.error("LoginUser Error:", error);
  }
}

export default function Login() {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = async (e) => {
    try {
      e.preventDefault();
      const response = await LoginUser({
        email,
        password,
      });
      console.log(response);
    } catch (err) {
      console.log(err.message);
    }
  };

  return (
    <div
      className="flex min-h-full flex-col justify-center px-6 py-12 lg:px-8"
      id="login"
    >
      <div className="sm:mx-auto sm:w-full sm:max-w-sm">

        <h2 className="mt-10 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900">
          Sign in to your account
        </h2>
      </div>

      <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
        <form className="space-y-6" onSubmit={handleSubmit} action="/api/v1/users/login" method="post">
          <div>
            <label
              htmlFor="email"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Email address
            </label>
            <div className="mt-2">
              <input
                id="email"
                type="email"
                name="email"
                onChange={(e) => setEmail(e.target.value)}
                value={email}
                autoComplete="email"
                required
                className="block pl-2 w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>

          <div>
            <div className="flex items-center justify-between">
              <label
                htmlFor="password"
                className="block text-sm font-medium leading-6 text-gray-900"
              >
                Password
              </label>
              <div className="text-sm">
                <a className="font-semibold text-indigo-600 hover:text-indigo-500" href="/">
                  Forgot password?
                </a>
              </div>
            </div>
            <div className="mt-2">
              <input
                id="password"
                name="password"
                type="password"
                onChange={(e) => setPassword(e.target.value)}
                value={password}
                placeholder="••••••••"
                autoComplete="current-password"
                required
                className="block pl-2 w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>

          <div>
            <button
              type="submit"
              className="flex w-full justify-center bg-indigo-600 rounded-lg border px-3 py-1.5 text-sm text-white"
            >
              Sign in
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
